	public HashMap<String, Oglas> getOglasi() {
		return oglasi;
	}

	public void setOglasi(HashMap<String, Oglas> oglasi) {
		this.oglasi = oglasi;
	}
	
	public Oglas findID(String naziv) {
		for(Oglas oglas: oglasi.values()) {
			if(naziv.equals(oglas.getNaziv())) {
				return oglas;
			}
			
		}
		return null;
	}
	
	public ArrayList<Oglas> oglasiPrikaz(){
		ArrayList<Oglas> aktuelni = new ArrayList<Oglas>();
		
		for(Oglas o: oglasi.values()) {
			if(o.getStanje().equals(Aktivan.AKTUELAN)) {
				aktuelni.add(o);
			}
		}
		return aktuelni;
	}
	
	
	
	
	public ArrayList<Oglas> oglasiPrikazTop(){
		ArrayList<Oglas> glavni = new ArrayList<Oglas>(this.oglasiPrikaz());
		Collections.sort(glavni);
		
		
		if(glavni.size() > 5) {
			glavni.subList(5, glavni.size()).clear();
		}	
		
		return glavni;
	}
	
	
	public ArrayList<Oglas> oglasiPrikazPoruceni(){
		ArrayList<Oglas> realizacija = new ArrayList<Oglas>();
		
		for(Oglas o: oglasi.values()) {
			if(o.getStanje().equals(Aktivan.REALIZACIJA)) {
				realizacija.add(o);
			}
		}
		return realizacija;
	}
	
	public ArrayList<Oglas> oglasiPrikazDostavljeni(){
		ArrayList<Oglas> dostavljeni = new ArrayList<Oglas>();
		
		for(Oglas o: oglasi.values()) {
			if(o.getStanje().equals(Aktivan.DOSTAVLJEN)) {
				dostavljeni.add(o);
			}
		}
		return dostavljeni;
	}
	
	public ArrayList<Oglas> oglasiPrikazKateg(Kategorija kategorija){
		ArrayList<Oglas> odg = new ArrayList<Oglas>();
		
		for(Oglas o: kategorija.getOglasi()) {
			if(o.getStanje().equals(Aktivan.AKTUELAN)) {
				odg.add(o);
			}
		}
		
		
		return odg;
	}
	
	public ArrayList<Oglas> findOglasi(ArrayList<String> nazivi) {
		ArrayList<Oglas> o = new ArrayList<Oglas>();
		for(Oglas oglas: oglasi.values()) {
			for (String naziv : nazivi) {
				if(naziv.equals(oglas.getNaziv())) {
					o.add(oglas);
				}
			}
		}
		return o;
	}

	@SuppressWarnings("unchecked")
	public void loadOglas(String contextPath) {
		FileWriter fileWriter = null;
		BufferedReader in = null;
		File file = null;
		try {
			file = new File(contextPath + "/oglasi.txt");
			in = new BufferedReader(new FileReader(file));

			ObjectMapper mapper = new ObjectMapper();
			mapper.disable(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES);
			TypeFactory factory = TypeFactory.defaultInstance();
			MapType type = factory.constructMapType(HashMap.class, String.class, Oglas.class);
			mapper.getFactory().configure(JsonGenerator.Feature.ESCAPE_NON_ASCII, true);
			oglasi = ((HashMap<String, Oglas>) mapper.readValue(file, type));

		} catch (FileNotFoundException fnfe) {
			try {
				file.createNewFile();
				fileWriter = new FileWriter(file);
				ObjectMapper objectMapper = new ObjectMapper();
				objectMapper.configure(SerializationFeature.INDENT_OUTPUT, true);
				objectMapper.getFactory().configure(JsonGenerator.Feature.ESCAPE_NON_ASCII, true);
				String kategString = objectMapper.writeValueAsString(oglasi);

				fileWriter.write(kategString);

			} catch (IOException e) {
				e.printStackTrace();
			} finally {
				if (fileWriter != null) {
					try {
						fileWriter.close();
					} catch (Exception e) {
						e.printStackTrace();
					}
				}
			}

		} catch (Exception ex) {
			ex.printStackTrace();
		} finally {
			if (in != null) {
				try {
					in.close();
				} catch (Exception e) {
					e.printStackTrace();
				}
			}
		}
	}
	
	
	public void saveOglas(String path, OglasDAO oglasi) {
		
		
		File f = new File(path + "/oglasi.txt");
		FileWriter fileWriter = null;
		try {
			fileWriter = new FileWriter(f);

			ObjectMapper objectMapper = new ObjectMapper();
			objectMapper.configure(SerializationFeature.INDENT_OUTPUT, true);
			objectMapper.getFactory().configure(JsonGenerator.Feature.ESCAPE_NON_ASCII, true);
			String kategString = objectMapper.writeValueAsString(oglasi.getOglasi());
			fileWriter.write(kategString);
			fileWriter.flush();
		} catch (IOException e) {
			e.printStackTrace();
		} finally {
			if (fileWriter != null) {
				try {
					fileWriter.close();
				} catch (Exception e) {
					e.printStackTrace();
				}
			}
		}
	}
	
	
	
